                title: rule.title,
                author: author,
                version: rule.version
            };
            depotStatus.ignoreUpdateRuleList.push(cacheIgnoreRule);
            writeDepotStatusToFile(depotStatus)
        } else {
            if (rule.version != cacheIgnoreRule.version) {
                cacheIgnoreRule.version = rule.version;
                writeDepotStatusToFile(depotStatus);
            } else {
                return true;
