catch (e) { banchSearch_config = {} }

if (!(banchSearch_config.threadCnt > 0)) banchSearch_config.threadCnt = 6
if (!(banchSearch_config.responseTime > 0)) banchSearch_config.responseTime = 5000
if (['内置', '原生'].indexOf(banchSearch_config.searchMode) == -1) banchSearch_config.searchMode = '内置'
if (!Array.isArray(banchSearch_config.history)) banchSearch_config.history = []
