var res = {};
var d = [];
// 使用 emoji 图标替换文本，使用/1 不使用/0
var useEmoji = 1;
// 帖子分类颜色
var nodeColor = "#2979FF";
// 是否显示点赞数评论数等，显示/1 不显示/0
var showLike = 1;

/**
 * 通过插件自动获取 Token 到文件中然后自动读取
 *
 * 注意：以上方的 token 为主
 */
var fileToken = getVar('hiker_token');
if (fileToken === '') {
try {
    fileToken = fetch ('hiker://files/rules/js/hikerbbs_token.js', {});
    if (fileToken == null) fileToken = '';
} catch (e) {
    fileToken = '';
}
}
var cookie = fileToken === '' ? '' : 'userToken=' + fileToken;
var headers = '';
if (cookie !== '') {
    headers = ';get;UTF-8;{User-Agent@Android&&Cookie@' + cookie + '}';
}

var html = getResCode();
var list = parseDomForArray(html, '.topic-list&&.topic-item');
for (let j = 0; j < list.length; j++) {
    let author = parseDomForHtml(list[j], '.topic-author&&Text');
    let metaItems = parseDomForArray(list[j], '.topic-meta&&.meta-item');
    // setError(JSON.stringify(metaItems));
    let metaNode = parseDomForHtml(metaItems[1], 'Text');
    let meta = parseDomForHtml(metaItems[0], 'Text')
        + '  '
        + '<font color="' + nodeColor + '">' + metaNode + '</font>';
    let metaTags = parseDomForArray('<div>' + metaItems[2] + '</div>', 'div&&.tag');
    // setError(JSON.stringify(metaTags));
    let metaTag = '';
    for (let i = 0; i < metaTags.length; i++) {
        metaTag = (metaTag === '' ? metaTag : (metaTag + '  '))
            + parseDomForHtml(metaTags[i], 'Text')
    }
    metaTag = metaTag.replace('置顶', '““置顶””');
    meta = meta + '  ' + metaTag;
    let desc = author + ' ““””' + meta;
    if (showLike) {
        let likeItems = parseDomForArray(list[j], '.like&&.like-item');
        // setError(JSON.stringify(list[j]));
        let likeCount = parseDomForHtml(likeItems[0], 'Text');
        let commentCount = parseDomForHtml(likeItems[1], 'Text');
        let seeCount = parseDomForHtml(likeItems[2], 'Text');
        let like = '获赞：' + (likeCount != null && likeCount !== 'undefined' && likeCount !== ''  ? likeCount : '0')
            + '\t\t'
            + '评论：' + (commentCount != null  && commentCount !== 'undefined' && commentCount !== '' ? commentCount : '0')
            + '\t\t'
            +'查看：' + (seeCount != null  && seeCount !== 'undefined' && seeCount !== '' ? seeCount : '0');
        // 👍🏻👍❤️🧡♥️💛🖤♡♥💬👁️➿𓁺
        if (useEmoji)
            like = like.replace("获赞：", "❤️ ")
                .replace("评论：", "💬 ")
                .replace("查看：", "👁️ ");
        desc = desc + '\n\n' + like;
    }
    let url = parseDom(list[j],'h1&&a&&href') + "#fypage" + "#noHistory#"
        + (headers === '' ? '' : headers);
    url = url.replace(/\n/g, '');
    // setError(url);
    // https://haikuoshijie.cn/topic/2982#fypage#noHistory#;GET;utf-8;{Cookie@userToken=xxxxxx}
    d.push({
        title: parseDomForHtml(list[j], 'h1&&Text'),
        desc: desc,
        url: url,
    });
}
res.data = d;
setHomeResult(res);

