  url: MY_URL,
  col_type: 'movie_1_vertical_pic'
}, {
  title: ['✅', '', ''][tabMode] + ' 磁链[' + Object.keys(tabs[0]).length + ']',
  url: $('#noLoading#').lazyRule(() => (putVar('wukongmeiju-tabMode', '0'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
}, {
  title: ['', '✅', ''][tabMode] + ' 云播[' + Object.keys(tabs[1]).length + ']',
  url: $('#noLoading#').lazyRule(() => (putVar('wukongmeiju-tabMode', '1'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
}, {
  title: ['', '', '✅'][tabMode] + ' 云盘[' + Object.keys(tabs[2]).length + ']',
  url: $('#noLoading#').lazyRule(() => (putVar('wukongmeiju-tabMode', '2'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
})

tabs[tabMode].forEach((v, i) => {
  let list = []
  if (tabMode == 0) {
    list = parseDomForArray(v, 'tbody&&tr').filter((v) => ! /<tr>\s*?<\/tr>/.exec(v))
    v = parseDomForHtml(v, 'thead&&th&&Text')
  } else list = parseDomForArray(lists[i], 'ul&&li')
  if (list.length) {
    d.push({ col_type: 'line_blank' }, {
      title: '““””<b><span style="color:#5cb85c">' + v + '</span></b>',
      url: 'hiker://empty',
      col_type: 'text_center_1'
    })
    list.forEach((vv) => {
      let url = parseDom(vv, 'a&&href'),
        title = parseDomForHtml(vv, tabMode == 0 ? 'input&&data-name' : 'a&&Text'),
        item = [{
          title: title,
          url: url + '#ignoreVideo=true#',
          col_type: 'text_' + [title.length > 5 ? 1 : 3, 4, 2][tabMode]
        }]
      if (tabMode == 1)
        item[0].url = $(url, '#cms_player&&Html').lazyRule(() => JSON.parse(/var cms_player = ({.*?})/.exec(input)[1]).url)
      else if (tabMode == 2) {
        let word = /【密码：(.*?)】/.exec(item[0].title) || []
        item[0].title+='📋'
        item[0].url = $(url).lazyRule((word) => {
          copy(word)
          return input
        }, word[1])
        if (v.indexOf('百度') >= 0) item.push({
          title: '复制盘口令',
          url: 'copy://' + url + ' 提取码: ' + word[1],
          col_type: 'text_3'
        })
      }
      Array.prototype.push.apply(d, item)
    })
  }
})
setResult(d)

