  if (/downcili-ico|down-ico/.exec(tabClass)) tabs[0][i] = parseDomForHtml(v, 'Text')
  else if (/playIco_yunbo/.exec(tabClass)) tabs[1][i] = parseDomForHtml(v, 'Text')
  else if (/playIco_dupan/.exec(tabClass)) tabs[2][i] = parseDomForHtml(v, 'Text')
})
//列表
let lists = parseDomForArray(html, '.box_bor3&&.tabs-list')

d.push({
  title: title,
  desc: desc,
  url: MY_URL,
}, {
  title: ['✅', '', ''][tabMode] + ' 磁链[' + Object.keys(tabs[0]).length + ']',
  url: $().lazyRule(() => (putVar('meijutt-tabMode', '0'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
}, {
  title: ['', '✅', ''][tabMode] + ' 云播[' + Object.keys(tabs[1]).length + ']',
  url: $().lazyRule(() => (putVar('meijutt-tabMode', '1'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
}, {
  title: ['', '', '✅'][tabMode] + ' 云盘[' + Object.keys(tabs[2]).length + ']',
  url: $().lazyRule(() => (putVar('meijutt-tabMode', '2'), refreshPage(), 'toast://已切换')),
  col_type: 'text_3'
})

tabs[tabMode].forEach((v, i, self) => {
  let list = parseDomForArray(lists[i], 'ul&&li')
  i = Object.keys(self).indexOf(i.toString())
  if (list.length) {
    d.push({ col_type: 'line_blank' }, {
      title: '““””<b><span style="color:#13b66a">' + v + '</span></b>',
      url: 'hiker://empty',
      col_type: 'text_center_1'
    })
    list.forEach((vv, ii) => {
      let url = parseDom(vv, 'a&&href'),
        item = [{
          title: parseDomForHtml(vv, 'a&&Text'),
          url: url + '#ignoreVideo=true#',
          col_type: 'text_' + [1, 4, 2][tabMode]
        }]
      if (tabMode == 1) {
        item[0].url = $(url).lazyRule((i, ii) => {
          eval(fetch(parseDom(fetch(input), '.ptitle&&script&&src')).split(',urlinfo=')[0])
          return VideoListJson[i][1][ii][1].replace(/\.[0-9](.*?)$/, '.$1')
        }, i, ii)
      } else if (tabMode == 2) {
        let word = parseDomForHtml(vv, 'span&&Text').slice(3)
        item[0].title = parseDomForHtml(vv, 'strong&&Text') + '  提取码: ' + word
        item[0].url = item.url = $('').x5Rule((url, word) => {
          let text = document.createElement('textarea')
          document.body.appendChild(text)
          text.value = word
          text.select()
          document.execCommand('Copy')
          document.body.removeChild(text)
          window.location.href = url
          alert('提取码已复制，自行粘贴至输入框')
        }, url, word)
        item.push({
          title: '复制盘口令',
          url: $(url + ' 提取码: ' + word).x5Rule(() => {
            let text = document.createElement('textarea')
            document.body.appendChild(text)
            text.value = input
            text.select()
            document.execCommand('Copy')
            document.body.removeChild(text)
            alert(input + '\n已复制')
          }),
          col_type: 'text_3'
        })
      }
      Array.prototype.push.apply(d, item)
    })
  }
})
setResult(d)

