var html = getResCode();

d.push({
    desc: '240&&float',
    col_type: 'x5_webview_single'
});

//动态解析
try {
    var lazy = `@lazyRule=.player_video&&script&&Html.js:eval(input);if(player_aaaa.url.indexOf('.html')==-1){var jssp = player_aaaa.url.split("\/share")[0];var json = fetch(player_aaaa.url,{});player_aaaa.url.indexOf("m3u8")>-1?player_aaaa.url:jssp+json.split(\'main = "\')[1].split(\'"\')[0]}else{refreshX5WebView('https://vip.parwix.com:4433/player/?url='+player_aaaa.url);'toast://缓冲中……'}`;
} catch(e) {}

//详情
try {
    var Rule = MY_URL+`@rule=js:let res={};let d=[];let html=getResCode();let otherInfo=parseDomForArray(html,'.content_detail&&ul');for(let i=0;i<otherInfo.length-1;i++){if(i==0){let setInfo=parseDomForHtml(otherInfo[i],'.data&&Text').match(/(分类：.*)(地区：.*)(年份：.*)/);for(let j=1;j<setInfo.length;j++){d.push({title:setInfo[j],col_type:'long_text'})}}else{d.push({title:parseDomForHtml(otherInfo[i],'.data,1&&Text'),col_type:'long_text'})}}d.push({title:'简介：'+parseDomForHtml(html,'.data,-2&&Text').replace(/　/g,''),col_type:'long_text'});res.data=d;setResult(res);`;
} catch(e) {}

//简介
try {
    var des_desc = parseDomForHtml(html, '.content_desc&&Text').replace(/　/g, '');
} catch(e) {}

//封面
try {
    var des_pic = parseDom(html, '.lazyload&&data-original');
} catch(e) {}

//线路列表
var tabs = parseDomForArray(html, '.play_source_tab&&a');

//播放列表
var conts = parseDom(getResCode(), '.container&&Html').match(/"playlist_notfull[\s\S]*?<\/ul>/g);;

d.push({
    title: '共' + conts.length + '条线路\n\n点击查看详情',
    desc: des_desc,
    pic_url: des_pic,
    url: Rule,
    col_type: 'movie_1_vertical_pic'
});

for (var i in tabs) {

    //线路名称
    try {
        d.push({
            title: parseDomForHtml(tabs[i], 'Text'),
            col_type: 'long_text'
        });
    } catch(e) {}

    //选集列表
    var list = parseDomForArray(conts[i], 'ul&&li');

    for (var j in list) {
        //选集名称以及播放链接
        try {
            d.push({
                title: parseDomForHtml(list[j], 'a&&Text'),
                url: parseDom(list[j], 'a&&href') + lazy,
                col_type: list.length > 2 ? 'text_3': 'text_2'
            });
        } catch(e) {}
    }
}
res.data = d;
setHomeResult(res);

