js:
var res = {};
var items = [];

// 所有开关 开则true 不开则false
// 顶部注意事项开关
let showWarning = true;
// 规则去重开关
let clearDuplicate = false;
// 自定义分类关键词列表
var keyList = ["分类关键词1", "分类关键词2"];
// 网络词库开关
let networkData = false;
// 网络词库链接
let keys_data_url = "https://cdn.jsdelivr.net/gh/fighting41love/funNLP/data/%E6%95%8F%E6%84%9F%E8%AF%8D%E5%BA%93/%E8%89%B2%E6%83%85%E8%AF%8D%E5%BA%93.txt";

// 从插件中读取分类关键词（分享用）
let keys_data_js_url = "hiker://files/rules/js/class_keys_data.js";
try {
    let keys_data_js = fetch(keys_data_js_url, {});
    let js_key_list = keys_data_js.split(/[\s\n]/);
    if (js_key_list != null && (typeof js_key_list !== "string") && js_key_list.length !== 0) {
        Array.prototype.push.apply(keyList, js_key_list);
    }
} catch (e) { }

if (networkData === true) {
// 从网络中读取分类关键词
    let keys_data = fetch(keys_data_url, {});
    keys_data = keys_data.split(/[\s\n]/);
    Array.prototype.push.apply(keyList, keys_data);
}

// 词库去重
keyList = Array.from(new Set(keyList));

let rules = JSON.parse(getResCode());

var mRulesList = new Array(keyList.length);
for (let i = 0; i < mRulesList.length; i++) {
    let rulesTemp = rules;
    mRulesList[i] = rulesTemp.filter(item => {
        let result = false;
        try {
            result = keyList[i] !== "" && (item.class_name + item.year_name + item.area_name).indexOf(keyList[i]) > -1;
        } catch (e) {  result = false;  }
        return result;
    });
}

function getIndexInArray(array, title) {
    for (let i = 0; i < array.length; i++) {
        if (array[i].title === title) return i;
    }
    return -1;
}

var keyNum = 0;
var itemsTemp = [];
for (let i = 0; i < mRulesList.length; i++) {
    let mRules = mRulesList[i];
    if (mRules.length > 0) {
        itemsTemp.push({
            title: keyList[i],
            col_type: "text_1"
        });
        keyNum++;
        for (let j = 0; j < mRules.length; j++) {
            let rule = mRules[j];
            itemsTemp.push({
                title: rule.title,
                url: "hiker://home@" + rule.title,
            });
        }
    }
}

if (clearDuplicate === true){
    let obj = {};
    itemsTemp = itemsTemp.reduce((current, next) => {
        obj[next.title] ? "" : obj[next.title] = true && current.push(next);
        return current;
    }, []);
}

if (showWarning === true) {
    items.push({
        title: "<b>本工具秉承\"““有杀错不放过””\"精神</b>",
        desc: "必然““存在误杀””，请谨慎处理！\n\n规则中有多个开关和自定义关键词，请自行配置~",
        col_type: "text_1"
    });
}

items.push({
    title: "总规则数：" + rules.length + "\n当前待处理：" + keyNum + " 组 / " + (itemsTemp.length - keyNum) + "条 规则",
    desc: "少掉的规则去哪里了？当然是不含关键词的规则啦！\n(然而还有一部分是匹配失败的结果🙈️...)",
    col_type: "pic_1"
});

while (itemsTemp.length) items.push(itemsTemp.shift());

res.data = items;
setHomeResult(res);

