function banchSearch_load() {
  var parseDom = parseDomForHtml
  function banchSearch_runCode(banchSearch_rule) {
    if (banchSearch_rule.includes('.js:')) {
      try {
        var input = banchSearch_rule.split('.js:')[0]
        return eval(banchSearch_rule.split('.js:')[1])
      } catch (e) {
        return banchSearch_rule
      }
    } else {
      return banchSearch_rule
    }
  }
  // æœç´¢ç­–ç•¥è¿‡æ»¤
  let banchSearch_filtersGroup = banchSearch_config.filters[banchSearch_config.apply].group,
    banchSearch_filtersExclude = banchSearch_config.filters[banchSearch_config.apply].exclude,
    banchSearch_homeList = JSON.parse(fetch('hiker://home')).filter((item) =>
      item.searchFind && item.title != MY_RULE.title
      && banchSearch_filtersGroup.find((v) => item.group == v)
      && !banchSearch_filtersExclude.find((v) => item.title == v))
      .slice((banchSearch_page - 1) * banchSearch_config.threadCnt, banchSearch_page * banchSearch_config.threadCnt)
  if (!banchSearch_homeList.length) return []
  let banchSearch_res = [], banchSearch_ruleList = [], banchSearch_urlList = []
  banchSearch_homeList.forEach((item) => {
    // ç¼“å­˜è§£æè§„åˆ™
    banchSearch_ruleList.push({
      title: item.title,
      searchFind: item.searchFind,
      sdetailFind: item.sdetail_find_rule == '*' ? item.detail_find_rule : item.sdetail_find_rule,
      col_type: item.sdetail_col_type,
      preRule: item.preRule,
      urlParam: item.search_url.match(/;.*/),
      ua: item.ua
    })
    // æœç´¢é“¾æ¥å¤„ç†
    let searchUrl = item.search_url
      .replace(item.search_url.indexOf('%%') >= 0 ? /%%/g : /\*\*/g, banchSearch_kw)
      .replace(/fypage(?:@(-?\d+)@)?(?:\*\d+@)?/, (_, $1) => parseInt($1 || 0) + 1)
      .split(';'),
      searchReq = { url: banchSearch_runCode(searchUrl[0]), options: { headers: {}, method: searchUrl[1], timeout: banchSearch_config.responseTime } }
    // postæ–¹æ³•æ—¶è½¬æ¢å‚æ•°
    if (/^post$/i.test(searchReq.options['method'])) {
      let [oriUrl, body] = searchReq.url.split('?')
      searchReq.url = oriUrl
      searchReq.options['body'] = body
    }
    if (searchUrl[2]) searchReq.options['content-type'] = 'application/json; charst=' + searchUrl[2]
    if (searchUrl[3]) searchUrl[3].match(/{(.*)}/)[1].split('&&').forEach((v) => {
      let headers = v.split('@')
      searchReq.options.headers[headers[0]] = banchSearch_runCode(headers[1])
    })
    // æ·»åŠ å…¨å±€UA
    if (!searchReq.options.headers['User-Agent']) {
      if (item.ua == 'pc')
        searchReq.options.headers['User-Agent'] = PC_UA
      else if (item.ua == 'mobile')
        searchReq.options.headers['User-Agent'] = MOBILE_UA
    }
    banchSearch_urlList.push(searchReq)
  })
  // è·å–æœç´¢ç»“æœ
  batchFetch(banchSearch_urlList).forEach((banchSearch_searchHtml, banchSearch_searchIndex) => {
    let banchSearch_itemRule = banchSearch_ruleList[banchSearch_searchIndex]
    try {
      if (!banchSearch_searchHtml && banchSearch_urlList[banchSearch_searchIndex].url.indexOf('hiker://') != 0) throw { message: 'æ— æ³•è®¿é—®ï¼Œæœªè·å–åˆ°ç½‘é¡µæºä»£ç ' }
      function banchSearch_urlHandle(url, addRule) {
        // æ·»åŠ åŸŸå
        if (!/^(https?|thunderx?|ed2k):\/\/|^magnet:\?xt=urn:btih:/i.test(url))
          url = banchSearch_urlList[banchSearch_searchIndex].url.match(/^(https?\:\/\/.*?)\//)[1] + url
        // æ·»åŠ äºŒçº§è§„åˆ™
        if (addRule && !/@(?:r|lazyR)ule=/.test(url)) {
          if (banchSearch_itemRule.sdetailFind)
            url += (banchSearch_itemRule.urlParam || '') + '@rule=' +
              banchSearch_itemRule.sdetailFind.replace('js:',
                'js:var setResult,setSearchResult,setHomeResult;' + $.toString((col_type) => {
                  setResult = function (func) {
                    return function (el) {
                      if (!Array.isArray(el)) el = el.data
                      func(el.map((v) => (v.col_type = v.col_type || col_type, v)))
                    }
                  }(setResult), setSearchResult = setResult, setHomeResult = setResult;
                }, banchSearch_itemRule.col_type))
          else url = 'web://' + url
        }
        // äºŒçº§é¡µé¢é”™è¯¯å¤„ç†
        if (url.indexOf('@rule=') >= 0) url = url.replace('js:',
          'js:try{') + '}catch(e){' + $.toString((title, banchSearch_reConfig) => {
            setResult([{
              title: 'å°ç¨‹åºã€' + title + 'ã€‘ä¸­ï¼Œæ­¤äºŒçº§é¡µé¢ä¸æ”¯æŒæˆ–è€…å·²å¤±æ•ˆï¼Œé”™è¯¯ä¿¡æ¯ï¼š\n\n' + JSON.stringify(e, null, 2).slice(2, -2),
              col_type: 'long_text'
            }, { col_type: 'line' }, {
              title: 'â€œâ€œâ€â€<span style="color:#409eff">æ’é™¤å°ç¨‹åº</span>',
              url: 'confirm://è¦æ’é™¤è¯¥å°ç¨‹åºå—?.js:'
                + banchSearch_reConfig('banchSearch_config.filters[banchSearch_config.apply].exclude.push("' + title + '")').replace('refreshPage(false)', '') + ';'
                + $.toString(() => writeFile('hiker://files/BatchSearchConfig.json', getVar('banchSearch-config')))
                + ';"toast://å·²æ’é™¤å°ç¨‹åºã€' + title + 'ã€‘"',
              col_type: 'text_center_1'
            }])
          }, banchSearch_itemRule.title, banchSearch_reConfig) + '}'
        return url
      }
      if (banchSearch_itemRule.searchFind.indexOf('js:') == 0) {
        // jsè§£æ
        Array.prototype.push.apply(banchSearch_res, function (MY_URL) {
          let banchSearch_result = []
          function getUrl() { return MY_URL }
          function getResCode() { return banchSearch_searchHtml }
          function setResult(el) {
            if (!Array.isArray(el)) el = el.data
            el.forEach((v) => {
              v.title = (banchSearch_config.searchMode == 'å†…ç½®' ? 'â€œâ€œâ€â€' : '') + v.title
              v.url = banchSearch_urlHandle(v.url, true)
              v.desc = banchSearch_itemRule.title + (v.desc ? ' ğŸ”¹ ' + v.desc : '')
              banchSearch_result.push(v)
            })
          }
          var setSearchResult = setResult, setHomeResult = setResult
          eval(banchSearch_itemRule.preRule)
          eval(banchSearch_itemRule.searchFind.slice(3))
          return banchSearch_result
        }(banchSearch_urlList[banchSearch_searchIndex].url))
      } else {
        // æ™®é€šè§£æ
        let find = banchSearch_itemRule.searchFind.split(';')
        parseDomForArray(banchSearch_searchHtml, find[0]).forEach((data) => {
          let [title, url, desc, content, img] = find.slice(1).map((v) =>
            banchSearch_runCode(v == '*' ? '' : parseDomForHtml(data, v))),
            item = {
              title: (banchSearch_config.searchMode == 'å†…ç½®' ? 'â€œâ€œâ€â€' : '') + title,
              url: banchSearch_urlHandle(url, true),
              desc: banchSearch_itemRule.title + (desc ? ' ğŸ”¹ ' + desc : ''),
              content: content,
              img: banchSearch_urlHandle(img)
            }
          if (item.title.indexOf(banchSearch_kw) >= 0 || item.desc.indexOf(banchSearch_kw) >= 0) banchSearch_res.push(item)
        })
      }
    } catch (e) {
      // æœç´¢ç»“æœé”™è¯¯å¤„ç†
      banchSearch_res.push({
        title: 'å°ç¨‹åºã€' + banchSearch_itemRule.title + 'ã€‘ä¸æ”¯æŒæˆ–è€…å·²å¤±æ•ˆï¼Œç‚¹å‡»æŸ¥çœ‹é”™è¯¯ä¿¡æ¯',
        url: 'confirm://' + JSON.stringify(e, null, 2).slice(2, -2) + '\n\næ˜¯å¦æ’é™¤è¯¥å°ç¨‹åº?.js:'
          + banchSearch_reConfig('banchSearch_config.filters[banchSearch_config.apply].exclude.push("' + banchSearch_itemRule.title + '")').replace('refreshPage(false)', '') + ';'
          + $.toString(() => writeFile('hiker://files/BatchSearchConfig.json', getVar('banchSearch-config')))
          + ';"toast://å·²æ’é™¤å°ç¨‹åºã€' + banchSearch_itemRule.title + 'ã€‘"',
        col_type: 'text_center_1'
      });
    }
  })
  return banchSearch_res
}
function banchSearch_reConfig(code) {
  return $.toString((code) => {
