  writeFile(_config.local_config_path, JSON.stringify(localConfig));
  d = [{
    title: 'è§„åˆ™ç‰ˆæœ¬: ' + getLocalRuleVersion() + '    æœ€æ–°: ' + remoteRuleVersion,
