      `localConfig.js_version='` + remoteJsVersion + `';` +
      `localConfig.setting=JSON.parse(\`` + remoteSetting + `\`);` +
      `writeFile('` + _config.local_config_path + `',JSON.stringify(localConfig));` +
      `refreshPage();'toast://æ›´æ–°æˆåŠŸï¼'`,
    col_type: 'text_1'
  }, {
    col_type: 'line_blank'
  }, {
    title: '<p style="text-align: center;"><strong><font color="red">Â·æ›´æ–°æ—¥å¿—Â·</font></strong><ul><li>' + remoteJson.note.join('</li><li>') + '</li></ul></p>',
    col_type: 'rich_text'
  }];
  res.data = d;
  setResult(res);
}
function displaySetting() {
  function findOptTitle(opts, value) {
    let title = '';
    opts.some((item) => {
      if (item.value == value) {
        title = item.title;
        return true;
      }
      else {
        return false;
      }
    });
    return title;
  }
  let res = {};
  let d = [];
  let setting = getLocalSetting();
  for (let i = 0; i < setting.length; i++) {
    d.push({
      title: setting[i].title + ': â€˜â€˜' + findOptTitle(setting[i].opts, setting[i].value) + 'â€™â€™',
      col_type: 'text_1'
    });
    for (let j = 0; j < setting[i].opts.length; j++) {
      d.push({
